import React from 'react';
import './RecentIdeas.css';
import './Card.css'; // Import shared card style

function RecentIdeas() {
  const ideas = [
    {
      title: 'AI-Powered Customer Support',
      status: 'Under Review',
      date: '2 days ago',
    },
    {
      title: 'SAP Prediction',
      status: 'Adopted',
      date: '5 days ago',
    },
    {
      title: 'Predictive Trend Analysis',
      status: 'In Development',
      date: '1 week ago',
    },
  ];

  return (
    <section className="card">
      <h2>Recent Ideas</h2>
      <div className="idea-list">
        {ideas.map((idea, index) => (
          <div key={index} className="idea-card">
            <h3>{idea.title}</h3>
            <p>Status: <strong>{idea.status}</strong></p>
            <p>Submitted: {idea.date}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default RecentIdeas;
